import React , { Component,useState ,useEffect}from 'react';
import { Layout, Menu, Image, message} from 'antd';
import {AppstoreOutlined,DesktopOutlined,NodeIndexOutlined} from '@ant-design/icons';

import {withNavigation} from '../helper/wrap'
import MyHeader from './header';

// import { getMenu } from '../../api/menu';

import logo from '../../logo.png'

import Provider from '../helper/provider'

import {getItem,getItems} from '../../util/tool'
const { Content, Footer, Sider } = Layout;



const items = [
  getItem('首页', '', <AppstoreOutlined />),
];

// const menuIcon = {
//   "":<AppstoreOutlined />,
//   "data":<DesktopOutlined />,
//   "resource":<NodeIndexOutlined />,
// }

class MyLayout extends Component {
  constructor(props){
    super(props)
    console.log(props)
    this.state = {
        collapsed: false,
        username:localStorage.getItem('name')||'',
        selected:[props.href.pathname.slice(1)||''],
        submenu:['data'],
        items:items
    };
    this.onItems = this.onItems.bind(this)
    this.onCollapse = this.onCollapse.bind(this)
    this.onMenu = this.onMenu.bind(this)
    this.onOpenChange = this.onOpenChange.bind(this)
    this.getChildFn = this.getChildFn.bind(this)
  }

  onItems = async ()=>{
    // const {username} = this.state
    // console.log(username)
    // if(username){
    //   console.log(username,'1111')
    //   const menu = await getMenu()
    //   console.log(menu)
    //   const menus=menu['menu'].map((item,index) => {
    //     if(item.children.length>0){
    //       return getItem(item.label,item.key,menuIcon[item.key],item.children)
    //       // return getItem(item.label,item.key,`<${item.icon }/>`,item.children)
    //     }else{
    //       return getItem(item.label,item.key,menuIcon[item.key])
    //       // return getItem(item.label,item.key,`<${item.icon} />`)
    //     }
        
    //   })
    //   console.log(menus)
    //   this.setState({
    //     items:menus.length>0?menus:items
    //   })
      
      
    // }else{
    //   console.log('......')
    //   this.setState({
    //     items
    //   })
    //   this.props.navigate(`/`,{replace:true})

      
    // }
    var menus = await getItems()
    console.log(menus)
    this.setState({
      items:menus.length>0?menus:items
    })
    // this.props.navigate(`/`,{replace:true})
  }
  
 
  componentDidMount(){
    const {username} = this.state
    console.log('layout mount',username)
    if(username){
      this.onItems()
    }else{
      message.info('Please signin')
    }
    
  }
  
  onCollapse = (collapsed) => {
    console.log(collapsed);
    this.setState({
      collapsed,
    });
  };
  
  
  onMenu=({ item, key, keyPath, domEvent })=>{
    console.log(item,key,keyPath,domEvent)
    this.setState({
      selected:[key]
    })
    this.props.navigate(`/${key}`, { replace: true })
  }

  onOpenChange=(submenu)=>{
    console.log(submenu)
    this.setState({submenu})
  }
  
  getChildFn = (e) => {
    console.log('child'+e);
    this.setState({
      username: e//把子组件传递过来的值赋给this.state中的属性
    },()=>{
      console.log(this.state)
      this.onItems()
    })
  }
  getMenu=(e)=>{
    console.log(e)
    this.setState({
      selected:[e]
    },()=>{
      console.log(this.state)
    })
  }
  render() {
    console.log(this.props)
    const { collapsed,selected,submenu ,items} = this.state;
    console.log(collapsed,selected,submenu ,items)

    return (
      <Layout
        style={{
          minHeight: '100vh',
        }}
      >
        <Sider collapsible collapsed={collapsed} onCollapse={this.onCollapse}>
            {collapsed?
            <div className="logo">
                <Image
                    src={logo}
                    fallback="data:image/png;base64,"
                />
            </div>
            :<div className="logo">
                <Image
                    src={logo}
                    fallback="data:image/png;base64,"
                />
                LIMS
            </div>}
          
          <Menu theme="dark" defaultSelectedKeys={selected}  selectedKeys={selected} defaultOpenKeys={submenu} openKeys={submenu} mode="inline" items={items} onClick={this.onMenu} onOpenChange={this.onOpenChange}/>
        </Sider>
        <Layout className="site-layout">
          <MyHeader getChild={this.getChildFn}></MyHeader>
          <Content
            style={{
              margin: '0 16px',
            }}
          >
            <div
              className="site-layout-background"
              style={{
                padding: 24,
                minHeight: 360,
              }}
            >
              {
              <Provider value={{menu:(menu)=>this.getMenu(menu)}}>
                {this.props.children}
              </Provider>
              }
            </div>
          </Content>
          <Footer
            style={{
              textAlign: 'center',
            }}
          >
            ©Yicon (Beijing) Biomedical Technology Inc.
          </Footer>
        </Layout>
      </Layout>
    );
  }
}

export default withNavigation(MyLayout)


